package hello.batch.springbatch.explorer_registry_operator;import org.springframework.batch.core.Job;import org.springframework.batch.core.Step;import org.springframework.batch.core.StepContribution;import org.springframework.batch.core.configuration.annotation.JobBuilderFactory;import org.springframework.batch.core.configuration.annotation.StepBuilderFactory;import org.springframework.batch.core.scope.context.ChunkContext;import org.springframework.batch.core.step.tasklet.Tasklet;import org.springframework.batch.repeat.RepeatStatus;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import lombok.RequiredArgsConstructor;@RequiredArgsConstructor@Configurationpublic class CustomJobConfiguration {	private final JobBuilderFactory jobBuilderFactory;	private final StepBuilderFactory stepBuilderFactory;	@Bean	public Job customJob() {		return jobBuilderFactory.get("customJob")			.start(step1())			.next(step2())			.build();	}	@Bean	public Step step1() {		return stepBuilderFactory.get("step1")			.tasklet((stepContribution, chunkContext) -> {				System.out.println("step1 execute");				return RepeatStatus.FINISHED;			})			.build();	}	@Bean	public Step step2() {		return stepBuilderFactory.get("step2")			.tasklet((stepContribution, chunkContext) -> {				System.out.println("step2 execute");				return RepeatStatus.FINISHED;			})			.build();	}}